        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{ product.name }}{% endblock %}

{% block content %}
    <h1>{{ product.name }}</h1>

    <div class=\"row\">

        <div class=\"col-md-4\">
                <img src=\"/uploads/{{ product.illustration}}\" alt=\"{{ product.name }}\" class=\"img-fluid\">
        </div>

        <div class=\"col-md-8 my-auto\">
            <h5>{{ product.name }}</h5>
            <p>{{ product.subtitle}}</p>
            <span class=\"product-page-price\">{{ (product.price / 100)|number_format(2, ',', '.')}} €</span>

            <hr>

            <a href=\"{{ path('add-to-cart', {'id' : product.id}) }}\" class=\"btn btn-primary\">Ajouter au panier</a>
        </div>        
    </div>
{% endblock %}
